function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

var asset=g_form.getValue('u_choice_6');
   if (asset == 'computer'){
	g_form.setValue('u_reference_9','de229b8783ee12107d8016dfeeaad3a4');
	}
	else if(asset == 'printer'){
	g_form.setValue('u_reference_9','63fe1f07832252107d8016dfeeaad3d8');
	}
	else if(asset == 'network gear'){
	g_form.setValue('u_reference_9','2c2e170b836e12107d8016dfeeaad3a0');
	}
	else if(asset == 'ups'){
	g_form.setValue('u_reference_9','2c9f1387832252107d8016dfeeaad3c8');
	}   
}
